margin = "0px";function reveal(animationTime, element, margin) {const ratio = animationTime;const options = {root: null,rootMargin: margin,threshold: ratio}; const handleIntersect = (entries, observer) => {entries.forEach((entry) => {if(entry.intersectionRatio > ratio) {entry.target.classList.add("reveal-visible")}else {entry.target.classList.remove("reveal-visible")}});};const observer = new IntersectionObserver(handleIntersect, options);document.querySelectorAll(element).forEach((r) => {observer.observe(r);});}